function displayCreateErrors(){let e=new URLSearchParams(window.location.search),r=e.get("err"),n=document.getElementById("error");if(r){switch(r){case"0":n.innerHTML="500 internal server error<br><br>",n.style.display="unset";break;case"1":n.innerHTML="email already in use<br><br>",n.style.display="unset";break;case"2":n.innerHTML="min password length is 8<br><br>",n.style.display="unset";break;case"3":n.innerHTML="invalid team join code<br><br>",n.style.display="unset"}history.replaceState(null,"",window.location.origin+window.location.pathname)}}