function displayErrors(){let e=new URLSearchParams(window.location.search),r=e.get("err"),s=document.getElementById("error");if(r)switch(r){case"0":s.innerText="500 internal server error",s.style.display="unset";case"1":s.innerText="email already in use",s.style.display="unset";case"2":s.innerText="min password length is 8",s.style.display="unset";default:return}}