function displayErrors(){let e=new URLSearchParams(window.location.search),n=e.get("err"),r=document.getElementById("error");if(n){switch(n){case"0":r.innerText="500 internal server error",r.style.display="unset";break;case"1":r.innerText="email already in use",r.style.display="unset";break;case"2":r.innerText="min password length is 8",r.style.display="unset"}history.replaceState(null,"",window.location.origin+window.location.pathname)}}