function displayErrors(){let e=new URLSearchParams(window.location.search),n=e.get("err"),t=document.getElementById("error");if(n){switch(n){case"0":t.innerText="500 internal server error",t.style.display="unset";case"1":t.innerText="email already in use",t.style.display="unset";case"2":t.innerText="min password length is 8",t.style.display="unset"}History.replaceState(null,"",window.location.origin+window.location.pathname)}}