"use strict";function getThemeCookie(){var e=RegExp("4c454a5b1bedf6a1=[^;]+").exec(document.cookie);return decodeURIComponent(e?e.toString().replace(/^[^=]+./,""):"")}function themeHandle(){let e=getThemeCookie();switch(e){case"light":document.body.classList.replace("dark-mode","light-mode"),document.getElementById("themeMeta").content="#ffffff";break;case"gruvbox":document.body.classList.replace("dark-mode","gruvbox"),document.getElementById("themeMeta").content="#282828";break;default:document.body.classList.replace("light-mode","dark-mode"),document.getElementById("themeMeta").content="#121212"}}const waitMs=e=>new Promise(t=>setTimeout(t,e));