function displayErrors(){let e=new URLSearchParams(window.location.search),r=e.get("err"),a=document.getElementById("error");if(r){switch(r){case"0":a.innerHTML="500 internal server error<br><br>",a.style.display="unset";break;case"1":a.innerHTML="bad email/password<br><br>",a.style.display="unset";break;case"2":a.innerHTML="account not yet approved by an admin<br><br>",a.style.display="unset"}history.replaceState(null,"",window.location.origin+window.location.pathname)}}