function displayLoginErrors(){let e=new URLSearchParams(window.location.search),r=e.get("err"),n=document.getElementById("error");if(r){switch(r){case"0":n.innerHTML="500 internal server error<br><br>",n.style.display="unset";break;case"1":n.innerHTML="bad email/password<br><br>",n.style.display="unset";break;case"2":n.innerHTML="account not yet approved by an admin<br><br>",n.style.display="unset"}history.replaceState(null,"",window.location.origin+window.location.pathname)}}