function displayErrors(){let e=new URLSearchParams(window.location.search),a=e.get("err"),n=document.getElementById("error");if(a){switch(a){case"0":n.innerText="500 internal server error",n.style.display="unset";break;case"1":n.innerText="bad email/password",n.style.display="unset";break;case"2":n.innerText="account not yet approved by an admin",n.style.display="unset"}history.replaceState(null,"",window.location.origin+window.location.pathname)}}