function displayErrors(){let e=new URLSearchParams(window.location.search),r=e.get("err"),t=document.getElementById("error");if(r)switch(r){case"0":t.innerText="500 internal server error",t.style.display="unset";case"1":t.innerText="bad email/password",t.style.display="unset";case"2":t.innerText="account not yet approved by an admin",t.style.display="unset";default:return}}